import time
from time import sleep
from time import strftime
import RPi.GPIO as GPIO
from picamera2 import Picamera2, Preview
from libcamera import controls
from datetime import datetime
import socket
 
experiment_duration = 1800
framerate = 40
resolution = (1400, 1400)
exposure = 5000
lensPosition = 12
rig_name = socket.getfqdn()

# Pin definitions
shutdown_pin = 2
record_pin = 27
stop_pin = 22
red_led = 23
yellow_led = 24

# Suppress warnings
GPIO.setwarnings(False)

# Use "GPIO" pin numbering
GPIO.setmode(GPIO.BCM)

# Set up GPIO input pins with internal pull-up resistors (push buttons)
GPIO.setup(shutdown_pin, GPIO.IN, pull_up_down=GPIO.PUD_UP)
GPIO.setup(record_pin, GPIO.IN, pull_up_down=GPIO.PUD_UP)
GPIO.setup(stop_pin, GPIO.IN, pull_up_down=GPIO.PUD_UP)

# Set up GPIO output pins (LEDs)
GPIO.setup(red_led, GPIO.OUT)
GPIO.setup(yellow_led, GPIO.OUT)

# Start with both LEDs off
GPIO.output(red_led,GPIO.LOW)
GPIO.output(yellow_led,GPIO.LOW)

# Yellow LED indicating RPi powered on
GPIO.output(yellow_led, GPIO.HIGH)

# Camera settings
picam2 = Picamera2()
video_config = picam2.create_video_configuration({'size': resolution})
picam2.configure(video_config)
picam2.set_controls({"AfMode": controls.AfModeEnum.Manual, "LensPosition": lensPosition, "FrameRate": framerate, "ExposureTime": exposure})

# Modular function to shutdown Pi
def shut_down():
    print("shutting down")
    command = "/usr/bin/sudo /sbin/shutdown -h now"
    import subprocess
    process = subprocess.Popen(command.split(), stdout=subprocess.PIPE)
    output = process.communicate()[0]
    print(output.decode())

while True:
    # Short delay to reduce CPU usage
    time.sleep(0.1)

    if GPIO.input(record_pin) == False:
        GPIO.output(red_led,GPIO.HIGH) # Red LED ON indicating recording start
        
        # Labels recording year-month-day_hour-minute-second
        date = datetime.now().strftime("%Y-%m-%d_%H-%M-%S")
        print("acquiring...")
        picam2.start_and_record_video(show_preview=True, output=f"/home/adultrig/data/{date}_{rig_name}.mp4", duration = experiment_duration)
        time.sleep(0.2)
        picam2.stop_recording()
        picam2.stop_preview()

        GPIO.output(red_led,GPIO.LOW) # Red LED OFF indicating recording end
        
        
